import React, { useState } from "react";
import { SketchPicker } from "react-color";
import './App.css';

function App() {
  const [rgbColor, setRgbColor] = useState({ r: 255, g: 128, b: 128 });
  const [displayColorPicker, setDisplayColorPicker] = useState(false);

  const handleChange = (color) => {
    setRgbColor(color.rgb);
  };

  const toggleColorPicker = () => {
    setDisplayColorPicker(!displayColorPicker);
  };

  return (
    <div className="App">
      <h1>Color Picker App</h1>
      
      <div className="color-picker-section">
        <div className="color-box" style={{ backgroundColor: `rgb(${rgbColor.r}, ${rgbColor.g}, ${rgbColor.b})` }}>
          rgb({rgbColor.r}, {rgbColor.g}, {rgbColor.b})
        </div>

        <div className="controls">
          <label>
            R:<input type="number" value={rgbColor.r} onChange={(e) => setRgbColor({ ...rgbColor, r: parseInt(e.target.value, 10) || 0 })} />
          </label>
          <label>
            G:<input type="number" value={rgbColor.g} onChange={(e) => setRgbColor({ ...rgbColor, g: parseInt(e.target.value, 10) || 0 })} />
          </label>
          <label>
            B:<input type="number" value={rgbColor.b} onChange={(e) => setRgbColor({ ...rgbColor, b: parseInt(e.target.value, 10) || 0 })} />
          </label>

          <button onClick={toggleColorPicker} className="open-palette-btn">Выбрать цвет из палитры</button>
          
          {displayColorPicker ? (
            <div className="color-picker-popover">
              <div className="color-picker-cover" onClick={() => setDisplayColorPicker(false)} />
              <SketchPicker color={rgbColor} onChange={handleChange} />
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
}

export default App;
