import React, { useState } from 'react';

function ColorPicker() {

  const [colorRGB, setColorRGB] = useState({ r: 255, g: 0, b: 0 });

  const [colorCMYK, setColorCMYK] = useState({ c: 0, m: 0, y: 0, k: 0 });

  const [colorLAB, setColorLAB] = useState({ l: 0, a: 0, b: 0 });
  
  // Функция для обновления цвета при вводе
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setColor({
      ...color,
      [name]: Math.max(0, Math.min(255, Number(value))) // ограничение 0-255
    });
  };

  // Функция для изменения цвета через ползунок
  const handleSliderChange = (e) => {
    const { name, value } = e.target;
    setColor({
      ...color,
      [name]: Number(value)
    });
  };

  // Отображение выбранного цвета
  const rgbColor = `rgb(${color.r}, ${color.g}, ${color.b})`;

  return (
    <div>
      <h2>Color Picker (RGB)</h2>

      {/* Отображение квадрата с цветом */}
      <div className="color-box" style={{ backgroundColor: rgbColor }} >
        {rgbColor}
      </div>

      {/* Поля ввода для точных значений */}
      <div>
        <label>R:
          <input type="number" name="r" value={color.r} onChange={handleInputChange} max="255" min="0" />
        </label>
        <label>G:
          <input type="number" name="g" value={color.g} onChange={handleInputChange} max="255" min="0" />
        </label>
        <label>B:
          <input type="number" name="b" value={color.b} onChange={handleInputChange} max="255" min="0" />
        </label>
      </div>

      {/* Ползунки для изменения цвета */}
      <div>
        <label>R:
          <input type="range" name="r" value={color.r} onChange={handleSliderChange} max="255" min="0" />
        </label>
        <label>G:
          <input type="range" name="g" value={color.g} onChange={handleSliderChange} max="255" min="0" />
        </label>
        <label>B:
          <input type="range" name="b" value={color.b} onChange={handleSliderChange} max="255" min="0" />
        </label>
      </div>

      {/* Выбор цвета через палитру */}
      <button
        onClick={() => document.getElementById('colorPicker').click()}
        style={{ padding: '10px 20px', cursor: 'pointer', marginTop: '20px' }}
      >
        Выбрать цвет из палитры
      </button>

      <input
        id="colorPicker"
        type="color"
        value={rgbColor}
        onChange={(e) => {
          const hex = e.target.value;
          const bigint = parseInt(hex.slice(1), 16);
          const r = (bigint >> 16) & 255;
          const g = (bigint >> 8) & 255;
          const b = bigint & 255;
          setColor({ r, g, b });
        }}
        style={{ display: 'none' }}
      />
    </div>
  );
}

export default ColorPicker;
